/* EDIT THE “MAIN COLORS” BELOW TO APPLY YOUR ORGANISATION’S COLORS */
/* Add your primary, secondary and tertiary colours by typing colour names such as "white" etc. or by replacing the hex color codes below. */
:root {
  --primary-palette-colour: #E0E0E0;
  --secondary-palette-colour: #FFFFFF;
  --primary-font-colour: #4F4F4F;
  --secondary-font-colour: #000000;
}

/* No need to touch the code below*/
#slides-view, #slides-view-inner, #lesson-menu {
  color: var(--secondary-font-colour) !important;
  background-color: var(--secondary-palette-colour) !important;
}
/*/ Buttons /*/
.btn.btn-muted,
.btn.btn-solid,
.btn.btn-primary {
  background: var(--primary-palette-colour);
  color: var(--primary-font-colour);
}
.btn.btn-muted.__active,
.btn.btn-muted:hover,
.btn.btn-solid.__active,
.btn.btn-solid:hover,
.btn.btn-primary__active,
.btn.btn-primary:hover {
  background: var(--primary-palette-colour);
  color: var(--primary-font-colour);
  filter: brightness(90%);
}
/* exclude game memory */
.slide-game-memory .game-tiles .btn.btn-solid {
  background: var(--primary-palette-colour);
  color: var(--primary-font-colour);
}
/*/ Header & Slides /*/
/*/ Corner Box /*/
#lesson-header-nav {
  background: #00000040;
  color: #fff;
}
/*/ Menu Button /*/
#lesson-header-nav-menu-btn {
  border-left: 1px solid var(--secondary-palette-colour);
}
/*/ Line at the Top/*/
#lesson-header-title-content {
  border-bottom: 1px solid var(--secondary-font-colour);
  color: var(--secondary-font-colour);
}
/*/ Progress Bar /*/
#indicator {
  background-color: #00000040;
  color: var(--primary-palette-colour);
}
/*/ Line at the bottom/*/
#slides {
  border-bottom: 1px solid var(--secondary-font-colour);
}
/*/ Image Background Color When You Zoom in/*/
.pswp__item {
  background: var(--primary-palette-colour);
}

/*/----- Multiple Choice Slides ------/*/
/*/Multiple Choice Slides Selected Button/*/
.selectable.active .btn {
  background-color: var(--secondary-palette-colour);
  color: var(--secondary-font-colour);
}
/*/Multiple Choice Incorrect/*/
.slide-multiple-choice-game.state-complete .selectable.incorrect .btn {
  background: var(--secondary-palette-colour);
  color: var(--secondary-font-colour);
}
/*/Multiple Choice Correct/*/
.slide-multiple-choice-game.state-complete .selectable.correct .btn {
  background: var(--primary-palette-colour);
  color: var(--primary-font-colour);
}
/*/-----------------------------------/*/

/*/Chat Bubble on selected/*/
.slide-chat.show-msg-true .message--answer.active {
  background: var(--primary-palette-colour);
  color: var(--primary-font-colour);
}
/*/Chat Bubble, the Pointy Part/*/
.slide-chat.show-msg-true .message--answer.active:after {
  border-color: var(--primary-palette-colour) transparent transparent;
}
/*/Expandable List/*/
.slide-expandable-list .item.active .item-button,
.slide-expandable-list .item.active .item-button:after {
  background: var(--secondary-palette-colour);
  color: var(--secondary-font-colour);
}
